#include<iostream>
#include "Logger.h"
#include "test.h"

void multitest(int threadId)
{
	int i=0;
	Logger *log = Logger::get();
	log->setLogLevel(ERROR);
	log->setLogStream("conctest.txt",true);
	while(i<1000)
	{
		LOG(INFO,"%2dth log message in info in thread-%d", i, threadId);
		i++;
	}
}

void planetest()
{
	Logger *log = Logger::get();
	LOG(INFO,"first log message in info");
	LOG(INFO,"%d log message in info", 1);
	LOG(INFO,"%d log message in %s", 1, "info");
	LOG(ERROR,"%d log message in %s", 1, "error"); //no log case

	log->setLogLevel(DEBUG);
	log->setLogStream("logfile.txt", true);
	LOG(INFO,"first log message in info");	//no log case
	LOG(ERROR,"first log message in info");	//no log case
	LOG(DEBUG,"%d log message in %s", 1, "debug");

	log->setLogLevel(ERROR);
	log->setLogStream("logfileerror.txt", true);
	LOG(INFO,"first log message in info");	
	LOG(ERROR,"first log message in %s:%d","error",1);
	LOG(DEBUG,"%d log message in %s", 1, "debug");

	LOG(ERROR,"");
	LOG(ERROR,"");

	if (log->setLogLevel(LOG_LEVEL_BUTT) != ERROR_INVALID_LOG_LEVEL)
		printf("Test case failed\r\n");
}